"""Tests for snipphthalate.context."""


import unittest
from unittest import mock

from snipphthalate import context


class _FakeContext(context.Context):
  """Fake Context implementation for testing Context itself."""

  def path(self):
    return super().path()

  def filetype(self):
    return super().filetype()


class ContextTest(unittest.TestCase):

  def test_path_not_implemented(self):
    with self.assertRaises(NotImplementedError):
      _FakeContext().path()

  def test_filetype_not_implemented(self):
    with self.assertRaises(NotImplementedError):
      _FakeContext().filetype()

  def test_prefixed_comment_is_none(self):
    self.assertIsNone(_FakeContext().prefixed_comment())

  def test_contained_comment_is_none(self):
    self.assertIsNone(_FakeContext().contained_comment())


class VimContextTest(unittest.TestCase):

  def setUp(self):
    super().setUp()
    # Unfortunately, the vim module doesn't seem to be available outside of vim.
    self._vim = mock.Mock()
    self._context = context.VimContext(self._vim)

  def test_path(self):
    for subtest, buffer_name, path in (
        ('empty', '', None),
        ('non_empty', 'kumquat', 'kumquat'),
    ):
      with self.subTest(subtest):
        self._vim.current.buffer.name = buffer_name
        self.assertEqual(path, self._context.path())

  def test_filetype(self):
    for subtest, buffer_filetype, filetype in (
        ('empty', b'', None),
        ('non_empty', b'python', 'python'),
    ):
      with self.subTest(subtest):
        self._vim.current.buffer.options = {'filetype': buffer_filetype}
        self.assertEqual(filetype, self._context.filetype())

  def test_prefixed_comment(self):
    for subtest, comments_option, expected in (
        ('not_found', b'fb:-', None),
        ('found', b'b:#,fb:-', '#'),
    ):
      with self.subTest(subtest):
        self._vim.current.buffer.options = {'comments': comments_option}
        self.assertEqual(expected, self._context.prefixed_comment())

  def test_contained_comment(self):
    for subtest, comments_option, expected in (
        ('invalid_order_of_components', b':,s:,:,s:,m:,:,e:', None),
        ('options_too_short', b's:,m:', None),
        ('no_padding', b'b:#,s:<!--,m:|,e:-->', ('<!--', '|', '-->')),
        ('with_padding', b's1:/*,mb:*,ex:*/', ('/*', ' *', ' */')),
    ):
      with self.subTest(subtest):
        self._vim.current.buffer.options = {'comments': comments_option}
        self.assertEqual(expected, self._context.contained_comment())


if __name__ == '__main__':
  unittest.main()
