"""Code for individual snippets."""


import abc
from typing import Any, Dict, Iterable, Optional

import jinja2

from snipphthalate import context
from snipphthalate import plugin


class RenderError(Exception):
  """Base error for snippet rendering."""


class Snippet(abc.ABC):
  """Base class for all snippets."""

  @abc.abstractmethod
  def render(
      self,
      context_: context.Context,
      plugins: Iterable[plugin.Plugin] = (),
      variant: Optional[str] = None) -> str:
    """Returns the expansion of this snippet.

    Args:
      context_: Context to expand the snippet in.
      plugins: Plugins that apply to the snippet.
      variant: Which variant of the snippet to render, or None for the default
        variant.

    Raises:
      RenderError: Rendering failed.
    """
    # TODO: Remove parameter defaults once registry supports all parameters.
    raise NotImplementedError()


class Jinja2Snippet(Snippet):
  """Snippet using a Jinja2 template."""

  def __init__(self, template: jinja2.Template):
    """Initializer.

    Args:
      template: Jinja2 Template to render.
    """
    self._template = template

  def render(
      self,
      context_: context.Context,
      plugins: Iterable[plugin.Plugin] = (),
      variant: Optional[str] = None) -> str:
    # TODO: Remove parameter defaults once registry supports all parameters.
    jinja2_context = {
        # TODO: Move this into a plugin that's loaded by default.
        'context': context_,
    }
    for plugin_ in plugin.recurse(plugins, plugin.Jinja2SnippetPlugin):
      plugin_.update_context(jinja2_context, context_, variant)
    try:
      return self._template.render(jinja2_context)
    except jinja2.TemplateError as e:
      raise RenderError('Error rendering Jinja2 template: {}'.format(e))
