"""Code for individual snippets."""


import abc
from typing import Any, Dict, Optional

import jinja2

from snipphthalate import context


class RenderError(Exception):
  """Base error for snippet rendering."""


class Snippet(abc.ABC):
  """Base class for all snippets."""

  def is_applicable(self, context_: context.Context) -> bool:
    """Returns whether or not the snippet is applicable to the given context.

    E.g., a snippet for a Python class would return False if the context is a
    C++ file.

    Args:
      context_: Editing context.
    """
    return True

  @abc.abstractmethod
  def render(self, context_: context.Context) -> str:
    """Returns the expansion of this snippet.

    Args:
      context_: Context to expand the snippet in.

    Raises:
      RenderError: Rendering failed.
    """
    raise NotImplementedError()


class Jinja2Snippet(Snippet):
  """Snippet using a Jinja2 template."""

  def __init__(self, template: jinja2.Template,
               default_context: Optional[Dict[str, Any]] = None):
    """Initializer.

    Args:
      template: Jinja2 Template to render.
      default_context: Defaults to use for the template context, or None for no
        defaults.
    """
    self._template = template
    self._default_context = default_context or {}

  def jinja2_context(self, context_: context.Context) -> Dict[str, Any]:
    """Returns the context to render the template with.

    The return value will be merged with the default context passed to the
    constructor, with keys returned here overriding the default.

    Args:
      context_: Snipphthalate context.

    Raises:
      RenderError: The context isn't usable. (Ideally, the is_applicable method
        would prevent this from ever happening.)
    """
    del context_  # Unused.
    return {}

  def render(self, context_: context.Context) -> str:
    jinja2_context = self.jinja2_context(context_)
    for key, value in self._default_context.items():
      jinja2_context.setdefault(key, value)
    try:
      return self._template.render(jinja2_context)
    except jinja2.TemplateError as e:
      raise RenderError('Error rendering Jinja2 template: {}'.format(e))
